    if gender_preference is not None:
        sex = gender_preference
    else:
        sex = get_gender()

    if sex == "–º—É–∂—á–∏–Ω–∞":
        profession = get_male_profession()
        marital_status = get_marital_status_male()
        hobby = get_male_hobby()
